Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('743','151','p02','<br />&#1058;&#1072;&#1079;&#1080; &#1091;&#1077;&#1073;-&#1089;&#1090;&#1088;&#1072;&#1085;&#1080;&#1094;&#1072; &#1080;&#1079;&#1087;&#1086;&#1083;&#1079;&#1074;&#1072; Unicode UTF-8. &#1040;&#1082;&#1086; &#1085;&#1103;&#1082;&#1086;&#1080; &#1086;&#1090; &#1088;&#1072;&#1079;&#1076;&#1077;&#1083;&#1080;&#1090;&#1077; &#1087;&#1086;&#1082;&#1072;&#1079;&#1074;&#1072;&#1090; &#1089;&#1090;&#1088;&#1072;&#1085;&#1085;&#1080; &#1079;&#1085;&#1072;&#1094;&#1080;, &#1074;&#1077;&#1088;&#1086;&#1103;&#1090;&#1085;&#1086; &#1077; &#1085;&#1091;&#1078;&#1085;&#1086; &#1076;&#1072; &#1089;&#1077; &#1087;&#1086;&#1076;&#1075;&#1086;&#1090;&#1074;&#1080; &#1082;&#1086;&#1084;&#1087;&#1102;&#1090;&#1098;&#1088;&#1072; &#1079;&#1072; &#1087;&#1088;&#1072;&#1074;&#1080;&#1083;&#1085;&#1086;&#1090;&#1086; &#1080;&#1084; &#1087;&#1086;&#1082;&#1072;&#1079;&#1074;&#1072;&#1085;&#1077;. &#1058;&#1086;&#1074;&#1072; &#1073;&#1080; &#1086;&#1079;&#1085;&#1072;&#1095;&#1072;&#1074;&#1072;&#1083;&#1086; &#1076;&#1072; &#1089;&#1077; &#1080;&#1085;&#1089;&#1090;&#1072;&#1083;&#1080;&#1088;&#1072;&#1090; &#1085;&#1103;&#1082;&#1086;&#1080; &#1080;&#1079;&#1090;&#1086;&#1095;&#1085;&#1080;&#1094;&#1080;.',null);
