Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('823','147',null,'<p><span lang="CA" style="FONT-SIZE: 12pt; FONT-FAMILY: " times="" new=""><span lang="CA" style="FONT-SIZE: 12pt; FONT-FAMILY: " times="" new=""><span lang="CA" style="FONT-SIZE: 8.5pt; FONT-FAMILY: Verdana; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: CA; mso-fareast-language: ES; mso-bidi-language: AR-SA; mso-bidi-font-family: ''Times New Roman''"><br /><span lang="CA" style="FONT-SIZE: 8.5pt; FONT-FAMILY: Verdana; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: CA; mso-fareast-language: ES; mso-bidi-language: AR-SA; mso-bidi-font-family: ''Times New Roman''">a)Recomanacions i normativa (recomanacions, informes, dict&agrave;mens i principis desenvolupats des d&rsquo;inst&agrave;ncies internacionals, europees, nacionals i auton&ograve;miques).<br style="mso-special-character: line-break" /><br style="mso-special-character: line-break" /></span><br style="mso-special-character: line-break" /><br style="mso-special-character: line-break" /></span></span></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1131','147',null,'<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span style="FONT-SIZE: 8.5pt; FONT-FAMILY: Verdana">La informaci&oacute; ha estat estructurada en once subseccions:<br /><br />- Interculturalitat i educaci&oacute;.<br />- Migracions.<br />- Pau, cooperaci&oacute; i desenvolupament<br />- Democracia i participaci&oacute;<br />- Drets humans<br />- G&egrave;nere i diversitat<br />- Mediaci&oacute;<br />- Informaci&oacute; solid&agrave;ria i alternativa<br />- Institucions i organitzacions nacionals i internacionals<br />- Fonts d&rsquo;informaci&oacute; a l&rsquo;educaci&oacute;<br />- Contes i narrativa oral<br /></span></p><p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span style="FONT-SIZE: 8.5pt; FONT-FAMILY: Verdana"></span></p><p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span style="FONT-SIZE: 8.5pt; FONT-FAMILY: Verdana"><br /><br />Si la teva organitzaci&oacute; ofereix continguts&nbsp;a la<personname productid="la Xarxa" w:st="on"></personname>&nbsp;Xarxa similars als esmentats i no ha estat inclosa al nostre mapa, si us plau, contactan&rsquo;s<p>&nbsp;</p></span></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('648','147',null,'<span style="FONT-SIZE: 8.5pt; FONT-FAMILY: Verdana; mso-fareast-font-family: ''Times New Roman''; mso-no-proof: yes; mso-ansi-language: ES; mso-fareast-language: ES; mso-bidi-language: AR-SA; mso-bidi-font-family: ''Times New Roman''">Obrim una gran finestra i fem un cop d&rsquo;ull a l&rsquo;exterior... Com s&oacute;n, com funcionen,&nbsp;en quins processos de reforma es troben involucrats els sistemes educatius d&rsquo;altres pa&iuml;sos?<br /><br />Les difer&egrave;ncies d&rsquo;estructura i continguts pel que fa al sistema educatiu espanyol, s&oacute;n reals o imagin&agrave;ries? Justifiquen mesures especials per als nostres nous alumnes?</span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('851','147',null,'<span style="FONT-SIZE: 8.5pt; FONT-FAMILY: Verdana; mso-fareast-font-family: ''Times New Roman''; mso-no-proof: yes; mso-ansi-language: ES; mso-fareast-language: ES; mso-bidi-language: AR-SA; mso-bidi-font-family: ''Times New Roman''">Mitjan&ccedil;ant aquesta secci&oacute; ens aproparem a les caracter&iacute;stiques b&agrave;siques d&rsquo;altres sistemes educatius, especialment d&rsquo;aquells dels quals procedeix el nostre alumnat, per tal de con&egrave;ixer millor el context educatiu del seus pa&iuml;sos d&rsquo;origen. Tamb&eacute; inclourem informaci&oacute; &uacute;til relativa a les homolacions, tant d&rsquo;ensenyaments universitaris com no universtaris: convenis en vigor, com sol&middot;licitar-les, a on adre&ccedil;ar-se...&nbsp;</span>',null);
