Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1120','11','p_01','Um aspecto relevante do nosso mundo &eacute; a conflu&ecirc;ncia e a necess&aacute;ria interliga&ccedil;&atilde;o entre pessoas com refer&ecirc;ncias culturais diversas. Talvez tenha sido sempre assim, mas no nosso&nbsp;momento presente, esta conflu&ecirc;ncia tem caracter&iacute;sticas e urg&ecirc;ncias concretas. Desde o &acirc;mbito educativo &eacute; preciso trabalhar <em>partindo do conceito amplo e plural de educa&ccedil;&atilde;o, </em>com uma <em>vis&atilde;o comunit&aacute;ria do facto educativo, </em>orientado para o desenvolvimento de uma nova cidadania, desej&aacute;vel e necessariamente intercultural.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2518','11','p_02','O caminho a essa nova cidadania passa pela progressiva adop&ccedil;&atilde;o de uma vis&atilde;o intercultural. A educa&ccedil;&atilde;o intercultural define-se ao mesmo tempo como um processo de mudan&ccedil;a e uma meta a alcan&ccedil;ar. O CREADE nasce como uma aposta valente e comprometida que pretende acompanhar e orientar este processo at&eacute; ao seu objectivo.',null);
