Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1127','159',null,'Loturen zerrenda eskaintzeaz haratago, Interneten baliabide, material, informazio edota prestakuntza eskainiz aniztasunaren alde lan egiten duten erakunde eta talde guztiak bilduko dituen sarearen mapa eratu nahi dugu. Topaleku gisa proposatu dugu, zabala eta etengabe eraikitzen, kultura artekotasunari buruzko eremu birtuala ehuntzen lagunduko diguten proposamen eta ekimenak gehituko ditugu bertan.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1131','159',null,'Informazioa hamaika azpiataletan egituratu dugu:<br /><br />-&nbsp;Kultura artekotasuna eta hezkuntza. <br />-&nbsp;Migrazioak<br />-&nbsp;Bakea, lankidetza eta garapena<br />-&nbsp;Demokrazia&nbsp;eta parte hartzea<br />-&nbsp;Giza eskubideak<br />-&nbsp;Generoa eta aniztasuna<br />- Bitartekaritza<br />-&nbsp;Informazio solidarioa eta alternatiboa<br />-&nbsp;Nazioarteko eta nazio mailako erakundeak<br />-&nbsp;Informazio iturriak heziketan<br />- Ipuinak eta ahozko narratiba<p>Zure erakundeak sarean aipatutakoen antzeko edukiak eskaintzen baldin baditu eta gure mapan sartu ez baditugu, mesedez, jar zaitez gurekin harremanetan.</p>',null);
