Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2897','0','¿Qué es?','<p>La Red INTER es un proyecto Comenius de la Comisi&oacute;n Europea, en el que participan 23 instituciones de 12 pa&iacute;ses europeos, entre ellas el Instituto de Formaci&oacute;n del Profesorado, Investigaci&oacute;n e Innovaci&oacute;n Educativa del Ministerio de Educaci&oacute;n en Espa&ntilde;a.&nbsp;<br />Partiendo de un enfoque intercultural que parte de la noci&oacute;n de diversidad como normalidad, la red pretende elaborar recursos y actividades para la formaci&oacute;n del profesorado para promover as&iacute; la interculturalidad en las pr&aacute;cticas escolares</p>',null);
