Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2940','0','Actuaciones','<p>Entre sus actuaciones cabe destacar:&nbsp;<br />- Programas de acogida socioling&uuml;&iacute;stica para el alumnado extranjero<br />- Flexibilizaci&oacute;n de la escolarizaci&oacute;n<br />- Tutor&iacute;as de acogida<br />- Aulas de acogida y acceso al curr&iacute;culo<br />- Aulas de inmersi&oacute;n ling&uuml;&iacute;stica<br />- Otras medidas como la sustituci&oacute;n de la optativa por el programa de refuerzo</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2941','0','Web ACOGIDA. Diversidad Cultural','Web de la Consejer&iacute;a de Educaci&oacute;n del Principado de Asturias destinada a dar apoyo a todo lo relacionado con la diversidad cultural.<br />As&iacute;, el Programa Acogida pretende ofrecer una respuesta a los nuevos retos que plantea la pluralidad cultural en las aulas, mediante la planificaci&oacute;n de actuaciones, el asesoramiento directo, la propuesta de materiales y recursos o actividades de formaci&oacute;n para el profesorado, con el objetivo de crear las condiciones &oacute;ptimas para una buena acogida en sus centros del alumnado llegado de otros pa&iacute;ses, as&iacute; como preparar a todo el alumnado para vivir en una sociedad culturalmente cada vez m&aacute;s diversa.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2883','112','Que é o CREADE?','<meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><meta name="ProgId" content="Word.Document" /><meta name="Generator" content="Microsoft Word 11" /><meta name="Originator" content="Microsoft Word 11" /><link rel="File-List" href="file:///C:\DOCUME~1\CRISTI~1\CONFIG~1\Temp\msohtml1\01\clip_filelist.xml" /><!--[if gte mso 9]><xml><w:WordDocument><w:View>Normal</w:View><w:Zoom>0</w:Zoom><w:HyphenationZone>21</w:HyphenationZone><w:PunctuationKerning /><w:ValidateAgainstSchemas /><w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid><w:IgnoreMixedContent>false</w:IgnoreMixedContent><w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText><w:Compatibility><w:BreakWrappedTables /><w:SnapToGridInCell /><w:WrapTextWithPunct /><w:UseAsianBreakRules /><w:DontGrowAutofit /></w:Compatibility></w:WordDocument></xml><![endif]--><!--[if gte mso 9]><xml><w:LatentStyles DefLockedState="false" LatentStyleCount="156"></w:LatentStyles></xml><![endif]--><style type="text/css"><!-- /* Font Definitions */ @font-face	{font-family:Verdana;	panose-1:2 11 6 4 3 5 4 4 2 4;	mso-font-charset:0;	mso-generic-font-family:swiss;	mso-font-pitch:variable;	mso-font-signature:536871559 0 0 0 415 0;}@font-face	{font-family:Calibri;	panose-1:2 15 5 2 2 2 4 3 2 4;	mso-font-charset:0;	mso-generic-font-family:swiss;	mso-font-pitch:variable;	mso-font-signature:-1610611985 1073750139 0 0 159 0;} /* Style Definitions */ p.MsoNormal, li.MsoNormal, div.MsoNormal	{mso-style-parent:"";	margin-top:0cm;	margin-right:0cm;	margin-bottom:10.0pt;	margin-left:0cm;	line-height:115%;	mso-pagination:widow-orphan;	font-size:11.0pt;	font-family:Calibri;	mso-fareast-font-family:Calibri;	mso-bidi-font-family:"Times New Roman";	mso-ansi-language:GL;	mso-fareast-language:EN-US;}@page Section1	{size:612.0pt 792.0pt;	margin:70.85pt 3.0cm 70.85pt 3.0cm;	mso-header-margin:36.0pt;	mso-footer-margin:36.0pt;	mso-paper-source:0;}div.Section1	{page:Section1;}--></style><!--[if gte mso 10]><style>/* Style Definitions */table.MsoNormalTable{mso-style-name:"Tabla normal";mso-tstyle-rowband-size:0;mso-tstyle-colband-size:0;mso-style-noshow:yes;mso-style-parent:"";mso-padding-alt:0cm 5.4pt 0cm 5.4pt;mso-para-margin:0cm;mso-para-margin-bottom:.0001pt;mso-pagination:widow-orphan;font-size:10.0pt;font-family:"Times New Roman";mso-ansi-language:#0400;mso-fareast-language:#0400;mso-bidi-language:#0400;}</style><![endif]--><span lang="GL" style="font-size: 8.5pt; line-height: 115%; font-family: Verdana;">O CREADE (Centro de Recursos para a Atenci&oacute;n &aacute; Diversidade Cultural en Educaci&oacute;n) &eacute; un proxecto do IFIIE (Centro de Formaci&oacute;n do Profesorado, Investigaci&oacute;n e Innovaci&oacute;n Educativa) e, polo tanto, do Ministerio de Educaci&oacute;n, que nace como resposta &aacute;s inquietudes dos e das profesionais do &aacute;mbito social e educativo respecto &aacute; diversidade cultural e &aacute;s s&uacute;as implicaci&oacute;ns, con vocaci&oacute;n de converterse nun referente tanto nacional coma internacional.</span>',null);
