Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1983','159',null,'<p><span lang="EU" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; mso-ansi-language: EU">Bigarren hizkuntzen irakaskuntza gero eta garrantzi handiagoa ari da hartzen kultura eta hizkuntzen dibertsitatearen inguruan. Hezkuntzaren alorrean, bizi garen eskualdeko komunikazio hizkuntza ezagutzea ezinbestekoa da sozializaziorako. Atal honek ama hizkuntza ez direnen irakaskuntza-ikaskuntza prozesurako mota guztietako materialak eskaintzen ditu: metodologia, proposamen didaktikoak, elkarreraginerako jarduerak eta autoikaskuntza.</span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1495','11','p_04','Entre as suas principais inten&ccedil;&otilde;es est&aacute; a de <em>reflectir com profundidade acerca do modelo de escola e de sociedade que queremos</em> e, ir articulando uma transforma&ccedil;&atilde;o social e educativa que deixe de destacar apenas alguns alunos para ir, paulatinamente, dirigindo-se a todos... Porque uma sociedade mais justa, mais igualit&aacute;ria, mais inclusiva e intercultural &eacute;, sem d&uacute;vida, algo colectivo. Trata-se da constru&ccedil;&atilde;o de uma nova cidadania na que todos e cada um merecemos ser protagonistas.',null);
