Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2631','0','Galicia Aberta','Portal de informaci&oacute;n sobre aspectos de inter&eacute;s para la&nbsp;poblaci&oacute;n de origen extranjero de Galicia,&nbsp;perteneciente a la Secretar&iacute;a General de Inmigraci&oacute;n.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2633','0',null,'<p>Material de apoyo al profesorado editado por la Xunta de Galicia en el a&ntilde;o 2005 sobre la acogida al alumnado extranjero. Define los objetivos del plan de acogida y las pautas generales para su elaboraci&oacute;n y comenta diversas medidas de actuaci&oacute;n as&iacute; como los aspectos m&aacute;s relevantes a tener en cuenta en la evaluaci&oacute;n del plan.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2634','0',null,'<p>Resultado de la colaboraci&oacute;n de las consejer&iacute;as de Educaci&oacute;n y Ordenaci&oacute;n Universitaria y de Emigraci&oacute;n, este folleto recoge las caracter&iacute;sticas principales del sistema educativo con el objetivo de facilitar la escolarizaci&oacute;n del alumnado procedente del extranjero. Incluye informaci&oacute;n sobre la estructura general del sistema educativo, el funcionamiento de los centros, los tr&aacute;mites de admisi&oacute;n y escolarizaci&oacute;n del alumnado y los servicios educativos complementarios.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2612','0','Atención al alumnado inmigrante','Secci&oacute;n del Portal Educativo del Gobierno Vasco en el que se ofrece informaci&oacute;n sobre diversas actuaciones educativas relacionadas con la atenci&oacute;n al alumnado inmigrante y la&nbsp;normativa&nbsp;vigente que las regula.<br />Dentro de esta secci&oacute;n podemos encontrar informaci&oacute;n espec&iacute;fica sobre el Programa de Atenci&oacute;n al Alumnado Inmigrante,&nbsp;Programas de Refuerzo Ling&uuml;&iacute;stico y Gu&iacute;as informativas sobre el sistema educativo en diferentes lenguas.',null);
