Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2670','0','UDICOM. Unidades Didácticas de Compensatoria.','Las unidades did&aacute;cticas van dirigidas a alumnos de Educaci&oacute;n Primaria con necesidades de compensaci&oacute;n educativa debido al desconocimiento de nuestro idioma o a un escaso vocabulario. Este material contiene una gu&iacute;a general orientativa y once unidades did&aacute;cticas en las que adem&aacute;s del lenguaje se trantan otras &aacute;reas de conocimiento como las matem&aacute;ticas,la educaci&oacute;n art&iacute;stica, el conocimiento del medio y varios temas de educaci&oacute;n transeversal.',null);
