Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2678','0','Curso interactivo "on line" de galego','O curso&nbsp;interactivo de galego disp&oacute;n de case un cento de actividades interactivas repartidas en catro secci&oacute;ns: comprensi&oacute;n, expresi&oacute;n, gram&aacute;tica e vocabulario. Ademais, ofr&eacute;celle &oacute; alumno materiais de consulta (vocabulario e gram&aacute;tica) e complementos (textos, ligaz&oacute;ns, etc.) que axudar&aacute;n a completa-la s&uacute;a formaci&oacute;n.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2679','0','Percepción sobre el mundo árabe: unidades didácticas.','Esta Unidad Did&aacute;ctica elaborada en el Centro de Investigaci&oacute;n para la Paz apoya en la reflexi&oacute;n sobre la imagen que tenemos desde occidente sobre el mundo &aacute;rabe. Nos habla del islam como civilizaci&oacute;n y como religi&oacute;n, del papel de los movimientos islamistas y de la situaci&oacute;n de las mujeres. Conocer m&aacute;s, es una buena herramienta contra el racismo.',null);
