Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2759','0','Tiny Piano','<p>&iquest;Te gustar&iacute;a tocar el piano pero no tienes ninguno cerca? Pues aqu&iacute; tienes la soluci&oacute;n: TinyPiano.c, una utilidad con la que podr&aacute;s convertir tu teclado en un piano virtual.&nbsp;<br />Si por algo destaca TinyPiano.c es por la gran cantidad de instrumentos que &ldquo;emula&rdquo;, desde una arm&oacute;nica, hasta un incontable n&uacute;mero de &oacute;rganos, guitarras y tambores.</p>',null);
