Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2799','0','Qué contiene','Ha puesto en marcha un centro de documentaci&oacute;n en educaci&oacute;n en valores e interculturalidad, cuyo portal est&aacute; en este momento en crecimiento: <a href="http://www.mnemosine.info">www.mnemosine.info</a>. Publica materiales educativos y de investigaci&oacute;n, que se pueden adquirir a trav&eacute;s del portal, donde adem&aacute;s hay libre acceso a otros documentos entre los que se incluyen las memorias de la asociaci&oacute;n.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2800','0','Qué es','La Asociaci&oacute;n Cultural Tremn es una ONGD que se dedica fundamentalmente a la Educaci&oacute;n para el Desarrollo con el criterio b&aacute;sico de la Interculturalidad desde diferentes niveles de actuaci&oacute;n. Desarrolla un programa dentro de los planes de cooperaci&oacute;n de la Junta de Extremadura, en centros educativos, centros penitenciarios, Universidades Populares, etc&hellip; Tambi&eacute;n lleva a cabo actividades en Andaluc&iacute;a y Galicia.',null);
