Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2804','0','Qué es','<p>Voces gitanas (Veus gitanes, Rromane glasura) es un medio de comunicaci&oacute;n del pueblo gitano y las mujeres que lo representan que pretende explicar en primera persona la cultura gitana actual en Catalu&ntilde;a, alej&aacute;ndose de los estereotipos, t&oacute;picos y prejuicios relacionados con la comunidad gitana.<br /></p>',null);
