Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2807','0','Qué contiene','Pone a disposici&oacute;n los atlas de la inmigraci&oacute;n marroqu&iacute; en Espa&ntilde;a de 1996 y 2004, desglosados en sus diferentes art&iacute;culos, todos ellos en formato pdf. Contienen datos estad&iacute;sticos y art&iacute;culos de an&aacute;lisis.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2808','0','Qué es','<p>Este observatorio nace con los objetivos de reunir y divulgar toda la informaci&oacute;n disponible sobre la inmigraci&oacute;n marroqu&iacute; en Espa&ntilde;a, mantener actualizada y explotar la base de datos sobre el origen (a nivel municipal) de los inmigrantes marroqu&iacute;es residentes en Espa&ntilde;a, evaluar el estado de la investigaci&oacute;n sobre el tema, manteniendo una base de datos con los trabajos realizados, y ofrecer un espacio de encuentro para las aportaciones de todos los investigadores.</p>',null);
