Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2811','0','Qué contiene','Permite consultar en red el cat&aacute;logo general y el de revistas. Contiene tambi&eacute;n informaci&oacute;n sobre el funcionamiento de la biblioteca y las nuevas adquisiciones, selecciones de recursos y los boletines de noticias de la biblioteca.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2812','0','Qué es','La Biblioteca de la Fundaci&oacute;n Pere Tarr&eacute;s est&aacute; especializada en ciencias sociales y m&aacute;s concretamente en aquellas tem&aacute;ticas afines al trabajo social y a la educaci&oacute;n social, la educaci&oacute;n ambiental y la educaci&oacute;n en el tiempo libre.',null);
