Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2816','0','p_09','Es tambi&eacute;n un centro de recursos virtual, que ofrece a trav&eacute;s de su portal web diversas bases de datos sistematizadas y especializadas en diferentes tipos de recursos: bibliogr&aacute;ficos y documentales, nuevas tecnolog&iacute;as, directorios, etc.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2817','0','p_08','Es un centro especializado que ofrece todo tipo de documentaci&oacute;n y recursos relacionados con la interculturalidad y la educaci&oacute;n, entendida &eacute;sta como proceso que acompa&ntilde;a de forma permanente a la experiencia humana.<br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2818','0','p_07','El CREADE pretende ser un lugar de di&aacute;logo y de reflexi&oacute;n en el que quienes se dedican a la educaci&oacute;n en su m&aacute;s amplio concepto encuentren referencias, material did&aacute;ctico, herramientas TIC, bibliograf&iacute;a, experiencias, investigaciones y, en general, recursos para la actuaci&oacute;n pedag&oacute;gica o la intervenci&oacute;n social desde un enfoque intercultural.<br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2819','0','p_11','El portal ofrece, asimismo, otras secciones dise&ntilde;adas en funci&oacute;n de las demandas y necesidades recogidas entre las personas que trabajan en el &aacute;mbito de la interculturalidad.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2821','0','Idiomas','Espa&ntilde;ol.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2822','0','Qué contiene','Cuenta con seis secciones: presentaci&oacute;n, banco bibliogr&aacute;fico (incluye res&uacute;menes de libros de psicolog&iacute;a evolutiva, educaci&oacute;n y pedagog&iacute;a considerados relevantes y, en concreto, aquellos que proponen soluciones pr&aacute;cticas a problemas educativos), los papeles de ME (cada trimestre se proponen temas para la movilizaci&oacute;n educativa sobre los cuales los usuarios registrados podr&aacute;n opinar), foro abierto sobre diversos temas, miscel&aacute;nea (distinta documentaci&oacute;n) y contacto con el webmaster, donde hacer sugerencias&nbsp;sobre el&nbsp;portal y su contenido.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2823','0','Qué es','Un espacio de colaboraci&oacute;n abierto en el que se pretende dar una respuesta com&uacute;n a los problemas en torno a la educaci&oacute;n, creado por el fil&oacute;sofo Jos&eacute; Antonio Marina. Parte de una pregunta b&aacute;sica ante la situaci&oacute;n educativa, &iquest;y yo qu&eacute; puedo hacer?, y pretende aprovechar la preocupaci&oacute;n, la generosidad, la energ&iacute;a y el talento de todas las personas dispuestas a colaborar en la b&uacute;squeda de un cambio cultural, necesario para mejorar la educaci&oacute;n.',null);
