Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2841','0','Jarabe de los idiomas','Dise&ntilde;ado por el Colegio de Farmac&eacute;uticos de Barcelona para que los ni&ntilde;os aprendan vocabulario relacionado con la salud. Tiene varios niveles de dificultad y el vocabulario se encuentra en los siguientes idiomas: alem&aacute;n, ingl&eacute;s, &aacute;rabe, vasco, b&uacute;lgaro, castellano, catal&aacute;n, franc&eacute;s, gallego, italiano, polaco, portugu&eacute;s, rumano, ruso, tagalo, ucranio, urdu, wolof y chino.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2839','0','Lenguas del Mundo ','Glosario en varias lenguas elaborado por alumnos del IES de diferentes nacionalidades. Incluye peque&ntilde;o vocabulario en catal&aacute;n y pasatiempos en: Ingl&eacute;s, Bengal&iacute;, Ilocano (filipinas), &Aacute;rabe, B&uacute;lgaro, Franc&eacute;s, Paixto (Paquist&aacute;n), Armenio, Hindi, Pangasinan (Filipinas), Panjabi (Punjab), Roman&eacute;s, Portugu&eacute;s, Quetxua (Per&uacute;), Ruso, Tagalog (Filipinas), Urdu (Paquist&aacute;n) y Chino <br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2840','0','Vocabulario básico en 80 lenguas ','P&aacute;gina que permite obtener un vocabulario b&aacute;sico biling&uuml;e de 80 idiomas (&aacute;rabe, chino, ruso, rumano...) podemos encontrar una serie de mini diccionarios biling&uuml;es que nos permitir&aacute;n conocer y hasta escuchar palabras y expresiones b&aacute;sicas en casi cualquier idioma del mundo.',null);
