Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2843','0','Matemáticas interactivas para alumnos inmigrantes en la ESO ','<p>P&aacute;gina realizada por el grupo Al-Karagi en la que se trabajan diferentes bloques tem&aacute;ticos c&oacute;mo los n&uacute;meros naturales, los decimales, las fracciones, el euro o la geometr&iacute;a. Dispone de un glosario con una serie de t&eacute;rminos traducidos al &aacute;rabe. <br /></p>',null);
