Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2926','112','Que é o CREI?','<p style="TEXT-ALIGN: justify; LINE-HEIGHT: normal; MARGIN: 0pt 0pt 10pt; BACKGROUND: white; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Verdana; FONT-SIZE: 8.5pt; mso-bidi-font-size: 12.0pt" lang="GL">&Eacute; un centro de <strong style="mso-bidi-font-weight: normal">car&aacute;cter rexional</strong> que lle proporciona apoio t&eacute;cnico e did&aacute;ctico &aacute; comunidade educativa sobre a atenci&oacute;n ao alumnado estranxeiro e de minor&iacute;as.</span><span lang="GL" times="" new=""><br /></span><span style="FONT-FAMILY: Verdana; FONT-SIZE: 8.5pt; mso-bidi-font-size: 12.0pt" lang="GL">Depende funcionalmente do Servizo de Atenci&oacute;n &aacute; Diversidade da Direcci&oacute;n Xeral de Planificaci&oacute;n, Ordenaci&oacute;n e Inspecci&oacute;n Educativa da Conseller&iacute;a de Educaci&oacute;n.</span><span lang="GL" times="" new=""><p>&nbsp;</p></span></p><p>&nbsp;</p><p>&nbsp;</p>',null);
