Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2928','112','Que é?','<p class="MsoNormal" style="margin: 0pt 0pt 10pt; background: white none repeat scroll 0% 0%; text-align: justify; line-height: normal; -moz-background-clip: border; -moz-background-origin: padding; -moz-background-inline-policy: continuous;"><span lang="GL" style="font-family: Verdana; font-size: 8.5pt;">&Eacute; un centro creado polo Departamento de Educaci&oacute;n, Cultura e Deporte do Goberno de Arag&oacute;n ao servizo da comunidade educativa aragonesa para todos os temas relacionados co acollemento e integraci&oacute;n da poboaci&oacute;n de orixe inmigrante no contexto educativo desde a &oacute;ptica da interculturalidade.</span><span lang="GL" mso-bidi-font-size:="" font-size:="" roman="" new="" times="" style=""><p>&nbsp;</p></span></p>',null);
