Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2946','0','PAIRE (Programa de Acogida, Integración y Refuerzo Educativo)','El Progama de Acogida, Integraci&oacute;n y Refuerzo Educativo surge como instrumento necesario para abordar la atenci&oacute;n a la diversidad creciente en los centros educativos, una diversidad cada vez m&aacute;s plural y compleja. Los objetivos de este Programa son: fomentar la inclusi&oacute;n social y escolar de todo el alumnado, consolidar la lengua catalana como lengua vehicular en los centros educativos y desarrollar la consciencia de igualdad y dignidad de todas las personas como marco de referencia para un di&aacute;logo intercultural.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2883','183','Qu''est-ce que le C.RE.A.D.E. ?','<span style="LINE-HEIGHT: 115%; FONT-FAMILY: Verdana; COLOR: #ff6600; FONT-SIZE: 10pt; mso-ansi-language: FR" lang="FR"><p style="TEXT-ALIGN: justify; MARGIN: 0cm 0cm 12pt; BACKGROUND: white" class="MsoNormal"><span style="LINE-HEIGHT: 115%; FONT-FAMILY: Verdana; FONT-SIZE: 10pt; mso-ansi-language: FR" lang="FR"><font color="#000000">Le C.RE.A.D.E. [Centre de REssources pour le Traitement de la Diversit&eacute; Culturelle en &Eacute;ducation] est un projet du C.I.D.E. [Centre de Recherche et de Documentation &Eacute;ducative] et, par cons&eacute;quent, du M.&Eacute;.S. [Minist&egrave;re de l&rsquo;&Eacute;ducation et des Sciences] qui na&icirc;t comme une r&eacute;ponse aux pr&eacute;occupations des professionnels/elles du domaine social et &eacute;ducatif, relatives &agrave; la diversit&eacute; culturelle et &agrave; ses implications et dont la vocation est de devenir une r&eacute;f&eacute;rence aussi bien nationale qu&rsquo;internationale.&nbsp;<p>&nbsp;</p></font></span></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p></span><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p style="TEXT-ALIGN: justify; MARGIN: 0cm 0cm 10pt" class="MsoNormal"><span style="LINE-HEIGHT: 115%; FONT-FAMILY: Verdana; COLOR: #ff6600; FONT-SIZE: 10pt; mso-ansi-language: FR" lang="FR"><p>&nbsp;</p></span></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p style="TEXT-ALIGN: justify; MARGIN: 0cm 0cm 12pt; BACKGROUND: white" class="MsoNormal"><span style="LINE-HEIGHT: 115%; FONT-FAMILY: Verdana; COLOR: #ff6600; FONT-SIZE: 10pt; mso-ansi-language: FR" lang="FR"><p>&nbsp;</p></span></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2874','183','Qu''est-ce que c''est ?','<p style="LINE-HEIGHT: normal; MARGIN: 0cm 0cm 0pt; BACKGROUND: white; mso-outline-level: 2" class="MsoNormal"><span style="FONT-FAMILY: Verdana; COLOR: green; FONT-SIZE: 10pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: FR; mso-fareast-language: ES" lang="FR"><font color="#000000">Dans le but de mettre en place une politique d&rsquo;int&eacute;gration des immigrants, le gouvernement espagnol a cr&eacute;&eacute; la Direction g&eacute;n&eacute;rale d&rsquo;Int&eacute;gration des Immigrants qui d&eacute;pend du Secr&eacute;tariat d&rsquo;&Eacute;tat &agrave; l&rsquo;Immigration et &agrave; l&rsquo;&Eacute;migration. Des comp&eacute;tences en mati&egrave;re de promotion de politiques d&rsquo;&eacute;galit&eacute; de traitement et de non-discrimination en raison de l&rsquo;origine raciale ou ethnique lui sont attribu&eacute;es. C&rsquo;est dans le cadre de ces comp&eacute;tences qu&rsquo;a &eacute;t&eacute; cr&eacute;&eacute; l&rsquo;Observatoire Espagnol du Racisme et de la X&eacute;nophobie, dot&eacute; de fonctions d&rsquo;&eacute;tude et d&rsquo;analyse et capable de faire des propositions d&rsquo;action, en mati&egrave;re de lutte contre le racisme et la x&eacute;nophobie.</font><p><font color="#000000">&nbsp;</font></p></span></p><p>&nbsp;</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2873','183','Quel est le contenu du portail ?','<p style="TEXT-ALIGN: justify; LINE-HEIGHT: normal; MARGIN: 0cm 0cm 10pt; BACKGROUND: white" class="MsoNormal"><span style="FONT-FAMILY: Verdana; COLOR: green; FONT-SIZE: 10pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: FR; mso-fareast-language: ES" lang="FR"><font color="#000000">Le portail de l&rsquo;Observatoire propose un centre de documentation et de donn&eacute;es, une r&eacute;glementation, un agenda d&rsquo;&eacute;v&eacute;nements, des liens pr&eacute;sentant un int&eacute;r&ecirc;t, une liste d&rsquo;entit&eacute;s et un moteur de recherche de programmes, sur les diff&eacute;rents domaines de discrimination. Il comporte &eacute;galement un Centre de Ressources Anti-Discrimination (C.RE.A.DI.).</font><p>&nbsp;</p></span></p><p>&nbsp;</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2926','183','Qu''est-ce que le C.R.É.I.?','<p style="TEXT-ALIGN: justify; LINE-HEIGHT: normal; MARGIN: 0cm 0cm 10pt; BACKGROUND: white; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Verdana; COLOR: green; FONT-SIZE: 10pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: FR; mso-fareast-language: ES" lang="FR"><font color="#000000">Il s&rsquo;agit d&rsquo;un centre &agrave; <strong>caract&egrave;re r&eacute;gional</strong> qui apporte un soutien technique et p&eacute;dagogique &agrave; la communaut&eacute; &eacute;ducative sur l&rsquo;attention &agrave; accorder aux &eacute;l&egrave;ves &eacute;trangers et issus de minorit&eacute;s.&nbsp;<br /></font></span><span style="FONT-FAMILY: Verdana; COLOR: green; FONT-SIZE: 10pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: FR; mso-fareast-language: ES" lang="FR"><font color="#000000">Du point de vue fonctionnel, il d&eacute;pend du Service d&rsquo;Attention &agrave; la Diversit&eacute;, de la Direction G&eacute;n&eacute;rale de la Planification, de l&rsquo;Am&eacute;nagement et de l&rsquo;Inspection &eacute;ducative de la Direction d&rsquo;&Eacute;ducation.</font><p>&nbsp;</p></span></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2928','183','Qu''est ce que c''est ?','<p style="TEXT-ALIGN: justify; LINE-HEIGHT: normal; MARGIN: 0cm 0cm 10pt; BACKGROUND: white; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Verdana; COLOR: green; FONT-SIZE: 10pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: FR; mso-fareast-language: ES; mso-bidi-font-weight: bold" lang="FR"><font color="#000000">Il s&rsquo;agit d&rsquo;un </font></span><span style="FONT-FAMILY: Verdana; COLOR: green; FONT-SIZE: 10pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: FR; mso-fareast-language: ES" lang="FR"><font color="#000000">centre cr&eacute;&eacute; par le D&eacute;partement de l&rsquo;&Eacute;ducation, de la Culture et des Sports du Conseil R&eacute;gional d&rsquo;Aragon au service de la communaut&eacute; &eacute;ducative aragonaise pour les th&egrave;mes en rapport avec l&rsquo;accueil et l&rsquo;int&eacute;gration de la population immigrante, s&rsquo;inscrivant dans le contexte &eacute;ducatif et vus du prisme de l&rsquo;interculturalit&eacute;.</font><p>&nbsp;</p></span></p><p>&nbsp;</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2626','0','Observatorio Municipal de la Igualdad','<p>El Observatorio Municipal de Igualdad es un organismo integrado en el &Aacute;rea de Educaci&oacute;n, Igualdad y Derechos C&iacute;vicos desde junio de 2007.<br />Es un instrumento de an&aacute;lisis de la realidad en el municipio de Palma de Mallorca.<br />Realiza diagn&oacute;sticos y estudios dirigidos al conocimiento y an&aacute;lisis de la realidad en temas relativos a la igualdad y los derechos c&iacute;vicos, abarcando colectivos tales como mujer, inmigraci&oacute;n y personas con discapacidad.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2948','0','Instituto para la Convivencia y el Éxito Escolar','<p>Organismo perteneciente a la Consejer&iacute;a de Educaci&oacute;n y Cultura de las Islas Baleares.<br />Dirigido a conocer, favorecer y difundir programas que faciliten la integraci&oacute;n del alumnado y el &eacute;xito escolar y, por tanto, que ayuden a crear un clima convivencial que tiene un efecto preventivo de los conflictos.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2949','0','Plan de Convivencia','El Plan de Convicencia para los Centros Educativos de Cantabria recoge, potencia y estructura las buenas pr&aacute;cticas que se realizan actualmente en los centros educativos, planteando diferentes l&iacute;neas de actuaci&oacute;n y acciones que responden a las nuevas demandas de la realidad educativa y del contexto socio-familiar del alumnado.',null);
