Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2883','17','Ce este CREADE?','CREADE (Centrul cultural pentru promovarea diversit&#259;&#539;ii culturale &icirc;n educa&#539;ie) este un proiect al IFIIE (Centrul de formare a profesoratului, de cercetare &#537;i inovare pedagogic&#259;) &#537;i, ca atare, al Ministerului educa&#539;iei, proiect care s-a constituit ca r&#259;spuns la preocup&#259;rile speciali&#537;tilor din mediul social &#537;i educativ cu privire la diversitatea cultural&#259; &#537;i la implica&#539;iile acesteia, cu scopul de a deveni un punct de referin&#539;&#259; na&#539;ional &#537;i interna&#539;ional.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2874','17','Ce este','Cu inten&#539;ia de a dezvolta politici de integrare a imigran&#539;ilor Guvernul a creat, &icirc;n cadrul Secretariatului de stat al imigr&#259;rii &#537;i emigr&#259;rii, Direc&#539;ia general&#259; de integrare a imigran&#539;ilor c&#259;reia i se atribuie competen&#539;e &icirc;n materie de promovare a politicilor de egalitate de drept &#537;i de nediscriminare rasial&#259; sau etnic&#259;. &Icirc;n baza acestor competen&#539;e a fost creat Observatorul spaniol &icirc;mpotriva rasismului &#537;i xenofobiei, cu func&#539;ii de studiu &#537;i analiz&#259;, av&acirc;nd capacitatea de a avansa propuneri de punere &icirc;n practic&#259; &icirc;n materie de lupt&#259; &icirc;mpotriva rasismului &#537;i a xenofobiei.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2873','17',null,'Pagina Observatorului ofer&#259; un centru de documentare &#537;i date, norme, o agend&#259; de evenimente, link-uri de interes, o list&#259; de entit&#259;&#539;i &#537;i un motor de c&#259;utare de programe, privind diversele cadre de discriminare. Include, de asemenea, un Centru de resurse pentru antidiscriminare (CREADI).',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2927','17','CREI','Centrul de resurse pentru educa&#539;ie intercultural&#259; al Consiliului regional din Castilla &#537;i León.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2926','17',null,'Este un centru cu caracter regional care aduce sprijin tehnic &#537;i didactic comunit&#259;&#539;ii educative dedicate elevilor str&#259;ini &#537;i minoritari. Din punct de vedere func&#539;ional depinde de Serviciul pentru promovarea diversit&#259;&#539;ii al Direc&#539;iei generale de planificare, reglementare &#537;i inspec&#539;ie educativ&#259; a Ministerului educa&#539;iei. ',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2929','17','CAREI','Centrul aragonez de resurse pentru educa&#539;ie intercultural&#259;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2928','17',null,'Este un centru creat de Departamentul de educa&#539;ie, cultur&#259; &#537;i sport al Guvernului Aragonului în slujba comunit&#259;&#539;ii educative aragoneze pentru toate chestiunile privitoare la primirea &#537;i integrarea popula&#539;iei de origine imigrant&#259;, în contextul educativ din perspectiva interculturalit&#259;&#539;ii. ',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2883','159','Zer da CREADE?','CREADE (Centro de Recursos para la Atenci&oacute;n a la diversidad Cultural en Educaci&oacute;n) IFIIE (Centro de Formaci&oacute;n del Profesorado, Investigaci&oacute;n e Innovaci&oacute;n Educativa) erakundearen eta, ondorioz, Hezkuntza Ministerioaren proiektua da, gizarte eta hezkuntza eremuko profesionalek&nbsp;kultur aniztasunaren eta haren&nbsp;ondorioen inguruko&nbsp;kezken ondorioz sortu dena eta nazio mailan zein nazio artekoan erreferente bilakatzeko xedea duena.',null);
