Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2874','159','Zer da','<p>Etorkinak barneratzeko politikak garatzeko asmoarekin, Gobernuak Etorkinak Barneratzeko Zuzendaritza Orokorra sortu zuen, Immigrazio eta Emigrazioko Estatuko Idazkaritzaren menpekoa, eta hari dagokio tratu berdintasuneko eta arraza zein etnian oinarrituta baztertzearen aurkako politikak sustatzea. Gaitasun horien esparruan, Arrazismoaren eta Xenofobiaren Espainiako Behatokia sortu zen eta haren funtzioa ikerketak eta azterketak burutzea da, eta arrazismoaren zein xenofobiaren aurka ekintzak proposatzea.</p>',null);
