Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2928','159','Zer da?','Aragoiko Gobernuaren Hezkuntza, Kultura eta Kirol Sailak sortutako zentroa da, Aragoiko hezkuntza komunitatearen zerbitzura dagoen&nbsp; eta kultura arteko ikuspuntutik, hezkuntzaren testuinguruan, etorkinak hartu eta barneratzearekin zerikusia duten gaien inguruanjarduten duena.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2883','11','O que é o CREADE?','<span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: PT; mso-fareast-language: ES; mso-bidi-font-family: ''Times New Roman''; mso-bidi-language: AR-SA" lang="PT">O CREADE (Centro de Recursos para a Aten&ccedil;&atilde;o &agrave; Diversidade Cultural na Educa&ccedil;&atilde;o) &eacute; um projecto do IFIIE (Centro de Forma&ccedil;&atilde;o do Professorado, Investiga&ccedil;&atilde;o e Inova&ccedil;&atilde;o Educativa) e, portanto, do Minist&eacute;rio da Educa&ccedil;&atilde;o, que nasce como resposta &agrave;s inquietudes dos e das profissionais do &acirc;mbito social e educativo com respeito &agrave; diversidade cultural e &agrave;s suas implica&ccedil;&otilde;es, com voca&ccedil;&atilde;o de se converter numa refer&ecirc;ncia tanto nacional como internacional.</span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2524','11','P_01','<p><span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-ansi-language: PT" lang="PT">O fomento da investiga&ccedil;&atilde;o e da inova&ccedil;&atilde;o educativas &eacute; um elemento que favorece a melhoria qualitativa do ensino e, por isso, o Minist&eacute;rio da Educa&ccedil;&atilde;o, atrav&eacute;s do IFFIIE, tem desenvolvido, desde h&aacute; muitos anos, um conjunto de iniciativas encaminhadas a promover a realiza&ccedil;&atilde;o, por parte dos membros da comunidade educativa, de actividades investigadoras e inovadoras que contribuam para renovar a pr&aacute;tica docente e a satisfazer as necessidades do sistema educativo.<p>&nbsp;</p></span></p><p><span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-ansi-language: PT" lang="PT">Em cumprimento desse objectivo e com a finalidade de contribuir desde o Sistema Educativo para incentivar o professorado ao impulsar a sua implica&ccedil;&atilde;o na investiga&ccedil;&atilde;o e inova&ccedil;&atilde;o educativa, foram criados, pela Ordem ECI/1709/2005 de 21 de Maio, BOE (Boletim Oficial do Estado) de 9 de Junho, os Pr&eacute;mios Nacionais de Investiga&ccedil;&atilde;o e Inova&ccedil;&atilde;o Educativa.<p>&nbsp;</p></span></p><p><span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-ansi-language: PT" lang="PT">Apesar de a convocat&oacute;ria ser aberta a trabalhos de tem&aacute;ticas muito diferentes, nos &uacute;ltimos anos foram objecto de aten&ccedil;&atilde;o preferente os relacionados com a educa&ccedil;&atilde;o para a paz, a preven&ccedil;&atilde;o da viol&ecirc;ncia e da intoler&acirc;ncia, a aten&ccedil;&atilde;o aos alunos imigrantes e a educa&ccedil;&atilde;o intercultural.<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2526','11','P_01','<p><span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-ansi-language: PT" lang="PT">Uma grande parte das condutas violentas s&atilde;o forjadas durante o processo de socializa&ccedil;&atilde;o, pelo que a escola, como espa&ccedil;o fundamental no qual se produz tanto a socializa&ccedil;&atilde;o como a cria&ccedil;&atilde;o de rela&ccedil;&otilde;es, deve orientar esse processo, conrtibuindo para eliminar, na nossa sociedade, a viol&ecirc;ncia em geral e, muito especialmente, a que &eacute; exercida contra as mulheres.<p>&nbsp;</p></span></p><p>&nbsp;</p><p><span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-ansi-language: PT" lang="PT">Por isso, para erradicar este tipo de comportamentos &eacute; necess&aacute;rio adoptar medidas de car&aacute;cter preventivo, dirigidas a promover uma educa&ccedil;&atilde;o baseada na igualdade, na n&atilde;o discrimina&ccedil;&atilde;o por raz&otilde;es de sexo e na resolu&ccedil;&atilde;o pac&iacute;fica de conflitos.<p>&nbsp;</p></span></p><p>&nbsp;</p><span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: PT; mso-fareast-language: ES; mso-bidi-font-family: ''Times New Roman''; mso-bidi-language: AR-SA" lang="PT">Com os Pr&eacute;mios Irene pretendem impulsar-se as experi&ecirc;ncias educativas, pautas de actua&ccedil;&atilde;o, materiais curriculares e de apoio, propostas pedag&oacute;gicas e todos os trabalhos inovadores que contribuam para prevenir e erradicar as condutas violentas e para promover a igualdade e a cultura da paz, procurando o desenvolvimento<span style="mso-spacerun: yes">&nbsp; </span>de estrat&eacute;gias de conviv&ecirc;ncia igualit&aacute;ria entre homens e mulheres. Essas experi&ecirc;ncias devem ter-se realizado durante os dois anos anteriores a cada convocat&oacute;ria e contar com uma aplica&ccedil;&atilde;o pr&aacute;tica nas aulas.</span>',null);
