Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2874','11','O que é','<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-ansi-language: PT" lang="PT">Com a inten&ccedil;&atilde;o de desenvolver pol&iacute;ticas de integra&ccedil;&atilde;o dos imigrantes, o Governo criou, dependendo da Secretaria de Estado da Imigra&ccedil;&atilde;o e Emigra&ccedil;&atilde;o, a Direc&ccedil;&atilde;o Geral de Integra&ccedil;&atilde;o dos Imigrantes &agrave; qual se atribuem compet&ecirc;ncias em mat&eacute;ria de promo&ccedil;&atilde;o de pol&iacute;ticas de igualdade de tratamento e n&atilde;o discrimina&ccedil;&atilde;o pela origem racial ou &eacute;tnica. No quadro destas compet&ecirc;ncias foi criado o Observat&oacute;rio Espanhol do Racismo e da Xenofobia, com fun&ccedil;&otilde;es de estudo e an&aacute;lise e capacidade para elevar propostas de actua&ccedil;&atilde;o em mat&eacute;ria de luta contra o racismo e a xenofobia.<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2873','11','O que contém','<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-ansi-language: PT" lang="PT">A<span style="mso-spacerun: yes">&nbsp; </span>p&aacute;gina do Observat&oacute;rio oferece um centro de documenta&ccedil;&atilde;o e dados, normas, agenda de eventos, links de interesse, lista de entidades e procura de programas, sobre os distintos &acirc;mbitos de discrimina&ccedil;&atilde;o. Tamb&eacute;m inclui um Centro de Recursos Anti-Discrimina&ccedil;&atilde;o (CREADI).<p>&nbsp;</p></span></p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2927','11','CREI','<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-ansi-language: PT" lang="PT">Centro de Recursos de Educa&ccedil;&atilde;o Intercultural, da Junta de Castilla y Le&oacute;n.<p>&nbsp;</p></span></p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2926','11','O que é o CREI?','<span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: PT; mso-fareast-language: ES; mso-bidi-font-family: ''Times New Roman''; mso-bidi-language: AR-SA" lang="PT">&Eacute; um centro de <strong style="mso-bidi-font-weight: normal">car&aacute;cter regional </strong>que oferece apoio t&eacute;cnico e did&aacute;ctico &agrave; comunidade educativa sobre a aten&ccedil;&atilde;o aos alunos estrangeiros e de minorias.<br />Depende funcionalmente do Servi&ccedil;o de Aten&ccedil;&atilde;o &agrave; Diversidade da Direc&ccedil;&atilde;o Geral de Planifica&ccedil;&atilde;o, Ordena&ccedil;&atilde;o e Inspec&ccedil;&atilde;o Educativa da Conselharia de Educa&ccedil;&atilde;o.</span>',null);
