Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2928','11','O que é?','<span style="FONT-FAMILY: Verdana; COLOR: black; FONT-SIZE: 7.5pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: PT; mso-fareast-language: ES; mso-bidi-font-family: ''Times New Roman''; mso-bidi-language: AR-SA" lang="PT">&Eacute; um centro criado pelo Departamento de Educa&ccedil;&atilde;o, Cultura e Desporto do Governo de Arag&atilde;o ao servi&ccedil;o da comunidade educativa aragonesa para todos os temas relacionados com a acolhida e integra&ccedil;&atilde;o da popula&ccedil;&atilde;o de origem imigrante, no contexto educativo desde a &oacute;ptica da interculturalidade.</span>',null);
