Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2928','89','Che cos''è?','<p>&Egrave; un centro creato dal Dipartimento di Educazione, Cultura e Sport del Governo di Aragona, al servizio della comunit&agrave; educativa aragonese, per ogni argomento relativo all''accoglienza e integrazione della popolazione&nbsp;immigrata nel contesto educativo, dal punto di vista dell''interculturalit&agrave;.</p>',null);
