Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3721','0','¿Qué es?','<p>La Asociaci&oacute;n de Ense&ntilde;antes con Gitanos es una Asociaci&oacute;n no gubernamental, civil, aconfesional, pluri&eacute;tnica, intercultural y apartidista, de car&aacute;cter socio-educativo y de funcionamiento democr&aacute;tico.</p><p>Especialmente vinculada al Pueblo Gitano, minor&iacute;a &eacute;tnica que, en el Estado espa&ntilde;ol, ha sufrido a lo largo de los &uacute;ltimos cinco siglos persecuciones, discriminaci&oacute;n y racismo.<br /></p>',null);
