Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3259','147','Butlletí','<span style="FONT-FAMILY: &quot;Times New Roman&quot;; FONT-SIZE: 12pt; mso-fareast-font-family: ''MS Mincho''; mso-ansi-language: CA; mso-fareast-language: JA; mso-bidi-language: AR-SA" lang="CA">El CREADE publica peri&ograve;dicament un butllet&iacute; que envia a les persones subscrites. En aquesta secci&oacute; es poden consultar els diversos butlletins publicats fins al moment. Si voleu rebre&rsquo;l, nom&eacute;s heu d&rsquo;introduir les vostres dades en el men&uacute; subscriu-t&rsquo;hi.</span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3259','112','Boletín','<span style="FONT-FAMILY: &quot;Times New Roman&quot;; FONT-SIZE: 12pt; mso-fareast-font-family: ''MS Mincho''; mso-ansi-language: GL; mso-fareast-language: JA; mso-bidi-language: AR-SA" lang="GL">O CREADE publica periodicamente un bolet&iacute;n, que lles env&iacute;a &aacute;s persoas subscritas. Nesta secci&oacute;n p&oacute;dense consultar os diferentes bolet&iacute;ns publicados ata o de agora. Para recibilo, s&oacute; tes que introducir os teus datos no men&uacute; subscr&iacute;bete</span>',null);
