Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3259','183','Bulletin','<p style="TEXT-JUSTIFY: inter-ideograph; TEXT-ALIGN: justify; MARGIN: 0pt" class="MsoNormal"><span lang="FR"><font size="3" face="Times New Roman">Le CREADE publie p&eacute;riodiquement un bulletin qu&rsquo;il envoie aux abonn&eacute;s. Il est possible de consulter &agrave; cette section les diff&eacute;rents bulletins publi&eacute;s jusqu&rsquo;&agrave; pr&eacute;sent. Pour les recevoir, tu n&rsquo;as qu&rsquo;&agrave; saisir tes donn&eacute;es dans le menu &laquo;&nbsp;Abonne-toi&nbsp;&raquo;.</font></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3259','182','Bulletin','<span lang="DE" new="" times="">CREADE ver&ouml;ffentlicht regelm&auml;&szlig;ig ein Bulletin, das an die Abonnenten verschickt wird. In diesem Abschnitt k&ouml;nnen Sie die bislang ver&ouml;ffentlichten Bulletins einsehen. Wenn Sie das Bulletin erhalten m&ouml;chten, geben Sie bitte Ihre Angaben unter <strong style="mso-bidi-font-weight: normal">Newsletter abonnieren</strong> ein.</span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3259','89','Bollettino','<span style="FONT-FAMILY: &quot;Times New Roman&quot;; FONT-SIZE: 12pt; mso-fareast-font-family: ''MS Mincho''; mso-ansi-language: IT; mso-fareast-language: JA; mso-bidi-language: AR-SA" lang="IT">Il CREADE pubblica periodicamente un bollettino che invia alle persone sottoscritte. In questa sezione si pu&ograve; consultare i vari bollettini pubblicati fino ad ora. Per riceverlo, devi solo introdurre i tuoi dati nel menu sottoscriviti</span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3259','11','Boletim','<span style="FONT-FAMILY: &quot;Times New Roman&quot;; FONT-SIZE: 12pt; mso-fareast-font-family: ''MS Mincho''; mso-ansi-language: PT; mso-fareast-language: JA; mso-bidi-language: AR-SA" lang="PT">O CREADE publica peri&oacute;dicamente um boletim, que envia &agrave;s pessoas que o subscreveram. Nesta sec&ccedil;&atilde;o podem consultar-se os diferentes boletins publicados at&eacute; ao momento. Para o receber s&oacute; tem de introduzir os seus dados no menu &ldquo;Subscrever</span>',null);
