Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3597','0',null,'<br />Sus <strong>objetivos</strong> se orientan fundamentalmente a:<br /><br />- la organizaci&oacute;n de un sistema educativo nacional apto para satisfacer las cambiantes necesidades de aprendizaje y de desarrollo nacional;<br />- al mejoramiento de la calidad y la eficiencia de la educaci&oacute;n;<br />- a la construcci&oacute;n de un sistema educativo intercultural y participativo;<br />- a la democratizaci&oacute;n de los servicios educativos partiendo de la plena cobertura en el nivel primario, hacia la ampliaci&oacute;n significativa de la cobertura en la educaci&oacute;n secundaria;<br />- a la promoci&oacute;n de los trabajos manuales, creativos y productivos, facilitando la profesionalizaci&oacute;n de ni&ntilde;os y j&oacute;venes en las especialidades requeridas por el desarrollo nacional.<br />',null);
