Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3697','0','¿Qué es?','<em>INTERCAMBIA. Educar en femenino y en masculino,</em>&nbsp;es un espacio virtual para compartir experiencias que reconocen los intereses, conocimientos y motivaciones de ni&ntilde;as, ni&ntilde;os, hombres y mujeres en la educaci&oacute;n. Est&aacute; creado para facilitar el acceso y el intercambio de informaci&oacute;n y conocimiento sobre pr&aacute;cticas educativas que incluyen los saberes femeninos y masculinos.&nbsp;Surge de&nbsp;una iniciativa del Ministerio de Educaci&oacute;n a trav&eacute;s del IFIIE (<span>Instituto de Formaci&oacute;n del Profesorado, Investigaci&oacute;n e Innovaci&oacute;n Educativa</span>) y del Ministerio de Igualdad a trav&eacute;s del Instituto de la Mujer, en colaboraci&oacute;n con los Organismos de Igualdad y las Administraciones Educativas de las Comunidades Aut&oacute;nomas.<br />&nbsp;<div class="parrafo_plantilla_texto"><div class="texto">&nbsp;</div></div>',null);
