Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3770','0','Educación Inicial','<p>Se organiza en 3 ciclos:<br />- Primer ciclo: atiende a ni&ntilde;os de 0 a 2 a&ntilde;os<br />- Segundo ciclo: atiende a ni&ntilde;os de 3 y 4 a&ntilde;os<br />- Tercer ciclo: atiende al alumnado de 5 a&ntilde;os.<br /><br />&Uacute;nicamente es obligatorio el &uacute;ltimo a&ntilde;o.<br /><br /></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3769','0','Educación Básica','Es obligatoria y atiende al alumnado de 6 a 14 a&ntilde;os. Tiene una duraci&oacute;n de 8 a&ntilde;os organizados en dos diclos de 4 a&ntilde;os cada uno.<br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3771','0','Educación Superior','El sistema de Educaci&oacute;n Superior comprende los Institutos Especializados de Estudios Superiores, los Institutos T&eacute;cnicos de Estudios Superiores y las Universidades:<br />&nbsp;&nbsp;&nbsp;&nbsp; -<em> Institutos T&eacute;cnicos de Estudios Superiores</em>: Ofrecen carreras &uacute;nicamente al nivel de t&eacute;cnico superior con programas de dos a tres a&ntilde;os de duraci&oacute;n.<br />&nbsp;&nbsp;&nbsp;&nbsp; -<em> Institutos Especializados de Estudios Superiores</em>: Pueden impartir carreras en los niveles de pregrado y postgrado en diferentes &aacute;reas de especialidad.<br />&nbsp;&nbsp;&nbsp;&nbsp; - <em>Universidades</em>: Imparten carreras al nivel de t&eacute;cnico, grado y postgrado. <br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3772','0','Educación Media','<p>No es obligatoria aunque es deber del estado ofrecerla gratuitamente. Atiende a alumnado entre 14 y 18 a&ntilde;os y est&aacute; organizada en un tronco com&uacute;n de dos a&ntilde;os de duraci&oacute;n y tres modalidades de dos a&ntilde;os de estudio cada una:<br />&nbsp;&nbsp;&nbsp;&nbsp; - <em>Modalidad General/Acad&eacute;mica</em>: Proporciona una formaci&oacute;n integral que permita al alumnado&nbsp;afianzar las bases para ingresar en el nivel superior.&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp; - &nbsp;<em>Modalidad T&eacute;cnico-Profesional</em>: Permite al alumnado obtener una formaci&oacute;n general y profesional que los ayude a adaptarse al cambio permanente de las necesidades laborales para ejercer e integrarse con &eacute;xito a las diferentes &aacute;reas de la actividad productiva y/o continuar estudios superiores. Esta modalidad ofrece diferentes menciones y especialidades, de acuerdo a las caracter&iacute;sticas y necesidades locales y regionales del pa&iacute;s.<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - <em>Modalidad de Artes</em>: Contribuye a la formaci&oacute;n de individuos en la comprensi&oacute;n, disfrute y promoci&oacute;n del arte, ofreciendo oportunidades para el desarrollo de competencias para el ejercicio de las profesiones y ocupaciones en el campo del arte o para proseguir estudios especializados en el mismo.<br /><br /></p>',null);
