Insert into CONTENIDO_AGENDA_I (ID_CONTENIDO,ID_IDIOMA,TITULO,CONTENIDO,SUBTITULO,LUGAR) values ('147','0','CURSOS DE VERANO EN VALENCIA: La cooperación al desarrollo y la comunidad educativa: una visión multipolar ','<p>Fecha: 10/09/2007 al 12/09/2007<br />C&oacute;digo: 70017<br />Sede:Valencia<br />Precio: 123 euros </p><p>Colaborador: <br />Fundaci&oacute;n Mainel </p>',null,'Universidad Internacional Menéndez Pelayo. Valencia');
Insert into CONTENIDO_AGENDA_I (ID_CONTENIDO,ID_IDIOMA,TITULO,CONTENIDO,SUBTITULO,LUGAR) values ('151','0','Congreso: El Español en una sociedad global.','&Aacute;reas de trabajo principales: El espa&ntilde;ol y las nuevas tecnolog&iacute;as, La ense&ntilde;anza del espa&ntilde;ol como segunda lengua, El espa&ntilde;ol como instrumento de expresi&oacute;n art&iacute;stica y El idioma como medio de promoci&oacute;n de la cultura. La elecci&oacute;n de estos t&oacute;picos pretende alcanzar dos objetivos: por una parte, reflexionar acerca del impacto provocado en la lengua por la constante incorporaci&oacute;n a la vida cotidiana de nuevos canales de comunicaci&oacute;n, como los mensajes a los tel&eacute;fonos m&oacute;viles o Internet; por otra, dotar a los profesionales de la ense&ntilde;anza de la lengua de instrumentos que eviten el deterioro de una riqueza cultural tan imprescindible como el uso del espa&ntilde;ol. Asimismo, el congreso intentar&aacute; ofrecer a los docentes un espacio en el que puedan conocer las novedades editoriales relativas al material did&aacute;ctico publicado sobre su especialidad.','La Fundación de la Lengua Española se complace en anunciar la celebración del congreso titulado El español en una sociedad global.','Valladolid');
