Insert into CONTENIDO_AGENDA_I (ID_CONTENIDO,ID_IDIOMA,TITULO,CONTENIDO,SUBTITULO,LUGAR) values ('376','0','VI Congreso de Educación "Educar para Pensar, Educar para Sentir"','<p><strong><em>P R O G R A M A</em></strong> </p><p><strong>CICLO I </strong>(Teatro Federico Garc&iacute;a Lorca)<br />Jueves, 11 de marzo</p><p><strong>19.30 h.</strong> Bienvenida y Apertura del Congreso:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Intervenci&oacute;n de las autoridades<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br /><strong>20.00 h. Conferencia Inaugural: </strong>&ldquo;Educaci&oacute;n + Inmigraci&oacute;n = &iquest;Racismo?&rdquo;</p><ul>    <li><strong>Pierre San&eacute;.&nbsp;</strong>&nbsp; Subdirector General de la UNESCO, &Aacute;rea de Ciencias&nbsp;&nbsp;&nbsp;&nbsp; Sociales y Humanas, ex Secretario General de de Amnist&iacute;a Internacional </li></ul><p>Presentado por <strong>Federico Mayor Zaragoza</strong>, Presidente de la Fundaci&oacute;n Cultura y Paz, ex Director General de la UNESCO<br /><br /><br />Los <strong>Ciclos II y III</strong> se realizar&aacute;n el mi&eacute;rcoles 17 de marzo (ver en la agenda)</p>','La Fundación Instituto de Cultura del Sur organiza el VI Congreso de Educación, bajo el título "Educar para Pensar, Educar para sentir", que se celebrará los días 11, 17 y 24 de Marzo en el Centro UNESCO de Getafe con el patrocinio de la Consejería de Inmigración y Cooperación de la Comunidad de Madrid.','Centro UNESCO Getafe. Teatro Federico García Lorca, C/ Ramón y Cajal, 22. Getafe (Comunidad de Madrid)');
