Insert into CONTENIDO_AGENDA_I (ID_CONTENIDO,ID_IDIOMA,TITULO,CONTENIDO,SUBTITULO,LUGAR) values ('228','0','Coloquio Internacional in Memoriam Manuel Alvar. Variacin lingstica y contacto de lenguas en el mundo hispnico.','<p>Este coloquio se centrar&aacute; en la exposici&oacute;n y debate desde distintas perspectivas ling&uuml;&iacute;sticas, ideol&oacute;gicas y pol&iacute;ticas de la situaci&oacute;n del espa&ntilde;ol en su diversidad y en contacto con otras lenguas. Se abordar&aacute;n en &eacute;l las siguientes &aacute;reas tem&aacute;ticas:</p><p>-Variedades y registros del espa&ntilde;ol.<br />-Procesos de normalizaci&oacute;n y estandarizaci&oacute;n de las lenguas o de determinadas variedades.<br />-Biling&uuml;ismo y contacto de lenguas en el &aacute;mbito hisp&aacute;nico.<br />-Variedades dialectales en la ense&ntilde;anza y en los medios de comunicaci&oacute;n</p><p>Organizan: <br />- Departamento de Lengua Espa&ntilde;ola, Ling&uuml;&iacute;stica y T&ordf; de la Literatura - Facultad de Filolog&iacute;a -<br />- Universidad de Sevilla <br />- Real Academia Sevillana de Buenas Letras</p><p>Direcci&oacute;n del Coloquio: <br />- Yolanda Congosto Mart&iacute;n <br />- Elena M&eacute;ndez Garc&iacute;a de Paredes</p>',null,'Sevilla, del 19 al 24 de noviembre de 2007');
