Insert into CONTENIDO_AGENDA_I (ID_CONTENIDO,ID_IDIOMA,TITULO,CONTENIDO,SUBTITULO,LUGAR) values ('285','0','Curso Ciudadana, Participacin Social y Convivencia Intercultural','<p>Organizacin:<br /><br />Escuela de Convivencia de Madrid. <br />C/ Pza d elos Reyes Magos 12<br />Metro Conde de Casal<br /><br />Contenidos:<br /><br />1. El contexto de la ciudadania hoy<br />Las migraciones fenomeno estructural<br />Modelos de gestin de la diversidad<br />Hacia una ciudadana intercultural<br /><br />2. Tres ejes sobre los que construir la ciudadania intercultural<br />Nuevas identidades<br />El sentido de pertencia<br />La comunicacin<br /><br />3. Campos de juego de la ciudadana<br />Procesos de discriminacin, exclusin y nuevo racismo<br />Oportunidades y lmites de distintas experiencias de participacin social (redes, movimientos, plataformas)<br /><br />4. La Cultura y la Interculturalidad<br />- Definicin de Cultura y aproximacin a su dimensin poltica<br />- Cultura, comunidad e identidad: El sentimiento de pertenencia y la cohesin social.<br />- El dinamismo del concepto Interculturalidad y Globalizacin<br />- Anlisis de ejemplos<br /><br />5. Situemos la "Convivencia" y la "Interculturalidad" en nuestro contexto actual.<br />- Del Multiculturalismo a la Interculturalidad.<br />- Ejemplos de ayer y de hoy<br />- Connotaciones del trmino Convivencia Intercultural en la actualidad.<br />- Anlisis de los dispositivos de "Convivencia Intercultural" y otros procesos<br /><br />6. La participacin ciudadana y la riqueza cultural<br />- Dimensiones de la convivencia<br />- Participacin motor de convivencia y riqueza cultural<br />- Prctica cultural como vehculo de participacin<br />- Los recursos culturales: El potencial del patrimonio cultural.<br />- Los eventos, la participacin y la promocin de una convivencia Intercultural<br />- Ejemplos</p><p>Destinatarios/as:<br /><br />Personas profesionales interesadas en el tema / Publico en general</p>','El objetivo del curso es analizar las oportunidades y desafos para la construccin de una ciudadana intercultural hoy y promover el dinamismo de la convivencia y la participacin social en contextos multiculturales.','Madrid, del 22 de abril al 13 de mayo');
Insert into CONTENIDO_AGENDA_I (ID_CONTENIDO,ID_IDIOMA,TITULO,CONTENIDO,SUBTITULO,LUGAR) values ('286','0','Jornadas Profesionales: Enseanza de Castellano e Integracin Social de Inmigrantes','<p>Organiza:</p><p>Escuela de Convivencia de Madrid. <br />C/ Pza d elos Reyes Magos 12<br />Metro Conde de Casal<br /><br />Lugar y fecha:<br /><br />Viernes, 25 de abril de 2008, de 10 a 14 h.<br /><br />Objetivos:<br /><br />&nbsp;Generales: <br />- Aportar propuestas metodol&oacute;gicas a los profesionales y voluntarios que trabajan la ense&ntilde;anza de castellano con la poblaci&oacute;n inmigrante en el municipio de Madrid.<br />- Evaluar las necesidades de espacios de formaci&oacute;n para promover el aprendizaje del castellano entre la poblaci&oacute;n inmigrante residente en Madrid.<br /><br />Espec&iacute;ficos:<br />- Abrir un espacio para el intercambio de buenas pr&aacute;cticas entre los t&eacute;cnicos que desarrollan la labor de ense&ntilde;anza de castellano..<br />- Optimizar los recursos ya existentes.<br />- Facilitar la coordinaci&oacute;n entre dispositivos p&uacute;blicos y dispositivos gratuitos de gesti&oacute;n privada unificando la calidad del servicio en castellanizaci&oacute;n.<br />- Dise&ntilde;ar una l&iacute;nea de trabajo pertinente, en el &aacute;mbito de la ense&ntilde;anza de castellano en la Escuela de Convivencia. </p><p>Contenidos:<br /><br />- Propuestas pedag&oacute;gicas para mejorar el proceso de aprendizaje del castellano<br />- Perfil del alumnado de este tipo de servicios en el municipio de Madrid.<br />- Buenas pr&aacute;cticas en vigor desde servicios p&uacute;blicos promovidos por la administraci&oacute;n.<br />- Nuevas tecnolog&iacute;as al servicio del aprendizaje de la lengua espa&ntilde;ola</p><p>Metodolog&iacute;a: </p><p>- Taller de Ense&ntilde;anza de castellano a partir del teatro<br />- Mesa redonda de expertos <br />- Grupos de trabajo con los profesionales asistentes en torno a los temas propuestos en los contenidos. <br />- Lectura de conclusiones de los grupos.<br />- Conclusiones y clausura de las jornadas.</p>',null,'Madrid');
