Insert into CONTENIDO_AGENDA_I (ID_CONTENIDO,ID_IDIOMA,TITULO,CONTENIDO,SUBTITULO,LUGAR) values ('454','0','I Congreso Internacional sobre Migraciones en Andaluca','<font color="#595959" face="BauhausITCbyBT-Light"><p align="left">El I Congreso Internacional sobre las Migraciones en Andaluc&iacute;a, organizado por el Instituto de Migraciones, que se celebrar&aacute; entre los d&iacute;as 16-18 de febrero de 2011 en Granada pretende ser un marco de intercambio y debate cient&iacute;fico para dar a conocer las investigaciones que en materia de migraciones se realizan sobre Andaluc&iacute;a.<br /><br />No se trata de un Congreso limitado a los investigadores e investigadoras de Andaluc&iacute;a, sino a aquellos que trabajan sobre dicho territorio desde cualquier parte del mundo en el &aacute;mbito de las migraciones.<br /><br />Pero, tambi&eacute;n, por las propias caracter&iacute;sticas del estudio de las migraciones, es necesario el abrirse a conocer lo que se realiza en cualquier otro centro de investigaci&oacute;n en la perspectiva de poder ayudar a la investigaci&oacute;n que se realiza en Andaluc&iacute;a.<br /><br /><strong>Calendario:</strong><br /><br /><table id="calendario">    <tbody>        <tr>            <td class="desc">Apertura de plazo para presentaci&oacute;n de Comunicaciones a las diferentes Mesas Tem&aacute;ticas del Congreso</td>            <td class="fecha">05-09-2010</td>        </tr>        <tr>            <td class="desc">Apertura del plazo para inscripciones en el Congreso</td>            <td class="fecha">05-09-2010</td>        </tr>        <tr>            <td class="desc">Circular segunda del Congreso. Recomendaciones de viaje y oferta de alojamiento.</td>            <td class="fecha">01-11-2010</td>        </tr>        <tr>            <td class="desc">Cierre del plazo de presentaci&oacute;n de comunicaciones a las diferentes Mesas Tem&aacute;ticas del Congreso</td>            <td class="fecha">10-12-2010</td>        </tr>        <tr>            <td class="desc">Reuni&oacute;n de Comit&eacute; Cient&iacute;fico para la selecci&oacute;n de Comunicaciones</td>            <td class="fecha">15-12-2010</td>        </tr>        <tr>            <td class="desc">Comunicaci&oacute;n de aceptaci&oacute;n de Comunicaci&oacute;n a sus autores o autoras</td>            <td class="fecha">20-12-2010</td>        </tr>        <tr>            <td class="desc">Circular tercera del Congreso. Listado de Comunicaciones</td>            <td class="fecha">20-12-2010</td>        </tr>        <tr>            <td class="desc">Fecha l&iacute;mite de inscripci&oacute;n en el Congreso con precio reducido (50 Euros)</td>            <td class="fecha">13-01-2011</td>        </tr>        <tr>            <td class="desc">Circular cuarta del Congreso. Presentaci&oacute;n general y localizaci&oacute;n</td>            <td class="fecha">20-01-2011</td>        </tr>        <tr>            <td class="desc">Celebraci&oacute;n del Congreso</td>            <td class="fecha">16-02-2011</td>        </tr>    </tbody></table></p></font>',null,'Universidad de Granada, Facultad de Ciencias Polticas y Sociologa.');
